var image = document.getelementbyid("image");
var i = 0;
var base = "/images/games/yokus-island-express/";

var images = [ "beginning.jpg", "monkey.jpg", "npcs.jpg", "table.jpg", "tweepers.jpg", "waterfall.jpg" ];

image.addeventlistener("click", function() {
    if (++i > images.length - 1) {
        i = 0;
    }

    image.src = base + images[i];
});
